import React from "react";
import "./App.css";
import { InputGroup, Input, Button } from "reactstrap";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { input: "", entries: "" };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(event) {
    const serverUrl = "http://localhost:5000";
    if (event.target.id === "read") {
      // console.log(this.state.input);
      fetch(serverUrl + "/read/" + this.state.input, { method: "GET" }).then(
        (response) => {
          return response.json().then((object) => {
            this.setState({
              entries: Object.entries(object),
            });
          });
        }
      );
    }
  }

  handleKeys(event) {
    this.setState({ input: event });
    // console.log(this.state.input);
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <ul>
            {Object.entries(this.state.entries).map(([key, value]) => (
              <li>
                <span>
                  {Object.values(this.state.entries[key][0])}:
                  {Object.keys(this.state.entries[key][1])[0]},
                  {Object.values(this.state.entries[key][1])}
                </span>
              </li>
            ))}
          </ul>
          <Input
            placeholder="#"
            onChange={(event) => this.handleKeys(event.target.value)}
            style={{ width: "50px" }}
          />
          <Input
            placeholder="key"
            onChange={(event) => this.handleKeys(event.target.value)}
            style={{ width: "100px" }}
          />
          <Input
            placeholder="value"
            onChange={(event) => this.handleKeys(event.target.value)}
            style={{ width: "100px" }}
          />
          <p>{this.state.input}</p>
          <Button
            outline
            color="secondary"
            size="lg"
            onClick={this.handleClick}
            id="create"
          >
            create
          </Button>
          <Button
            outline
            color="primary"
            size="lg"
            onClick={this.handleClick}
            id="read"
          >
            read
          </Button>
          <Button
            outline
            color="warning"
            size="lg"
            onClick={this.handleClick}
            id="update"
          >
            update
          </Button>
          <Button
            outline
            color="danger"
            size="lg"
            onClick={this.handleClick}
            id="delete"
          >
            delete
          </Button>
          <Button
            outline
            color="info"
            size="lg"
            onClick={this.handleClick}
            id="write"
          >
            write
          </Button>
          <Button
            outline
            color="info"
            size="lg"
            onClick={this.handleClick}
            id="load"
          >
            load
          </Button>
        </header>
      </div>
    );
  }
}

export default App;
