import React from "react";
import "./App.css";
import { Input, Button } from "reactstrap";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(event) {
    let state = this.state;
    const serverUrl = "http://localhost:5000";
    console.log(this.state, event.target.id);
    fetch(serverUrl + "/read/all", { method: "GET" }).then((response) => {
      return response.json().then((object) => {
        // state=object
        // for (var i in Object.keys(object)) {
        //   var id = Object.keys(object)[i];
        //   // console.log(id, Object.values(object[id]));
        //   // state["id"] = id;
        //   // state["key"] = Object.keys(object[id]);
        //   // state["value"] = Object.values(object[id]);
        //   // console.log(state);
        //   console.log(object);
        // }
        // console.log(Object.keys(object))
        state = object;
        this.setState(state);
        console.log(state);
      });
    });
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h3>id: {this.state["id"]}</h3>
          <h3>key: {this.state["key"]}</h3>
          <h3>value: {this.state["value"]}</h3>
          <Button color="success" onClick={this.handleClick} id="readAll">
            read all
          </Button>
        </header>
      </div>
    );
  }
}

export default App;

